export const createMessage = (id, patterns) => ({
    id,
    selectors: [],
    variants: Object.entries(patterns).map(([languageTag, patterns]) => ({
        languageTag,
        match: [],
        pattern: typeof patterns === "string"
            ? [
                {
                    type: "Text",
                    value: patterns,
                },
            ]
            : patterns,
    })),
});
